//! The only purpose of this crate is to relegate the functions generated by
//! the encode! and decode! syntax extensions of pfor_macros to a separate
//! compilation unit. More extensive documentation is given in the pfor_macros
//! crate.

#![feature(plugin)]
#![plugin(pfor_macros)]

#![allow(unused_mut)]

extern crate simd;

encode_native!(u8, 8);
decode_native!(u8, 8);
encode_native!(u16, 16);
decode_native!(u16, 16);
encode_native!(u32, 32);
decode_native!(u32, 32);
encode_native!(u64, 64);
decode_native!(u64, 64);

encode_simd!(u8, 8, simd::u8x16);
decode_simd!(u8, 8, simd::u8x16);
encode_simd!(u16, 16, simd::u16x8);
decode_simd!(u16, 16, simd::u16x8);
encode_simd!(u32, 32, simd::u32x4);
decode_simd!(u32, 32, simd::u32x4);
encode_simd!(u64, 64, simd::x86::sse2::u64x2);
decode_simd!(u64, 64, simd::x86::sse2::u64x2);
